package dled.github

import io.vlingo.actors.World
import io.reactivex.internal.util.ExceptionHelper.terminate
import java.math.BigDecimal
import io.vlingo.actors.Definition


/*
 * This Java source file was generated by the Gradle 'init' task.
 */
class App {
    fun run() {
        val world = World.start("playground")
        try {
            val processor = world.actorFor(
                    Definition.has(BatchProcessor::class.java, Definition.NoParameters),
                    Processor::class.java
            )

            processor.process(listOf<Item>(Item(1),Item(2),Item(3)))
        } catch (e: Exception) {
            e.printStackTrace()
        } finally {
            // waits for quiescence
            world.terminate()
        }
    }

    companion object {
        @JvmStatic
        fun main(args: Array<String>) {
            App().run()
        }
    }
}
